resources.description:
Creates a new invitation which also sends an email to the invited user

<!-- RESPONSE 200 -->
{
  "data": {
    "type": "invitation",
    "attributes": {
      "email": "invitee@kulturdaten.berlin",
      "user_id": 1,
      "created_at": "2021-04-27T12:41:04.960+02:00",
