resources.description:
Updates an organizer.

<!-- RESPONSE 200 -->
{
  "data": [
    {
      "id": "ckpgzs4f90004zt52es1q00sb",
      "type": "organizer",
      "attributes": {
        "name": "Staatliche Museen zu Berlin",
        "description": "With its numerous institutions that have evolved over generations, the Staatliche Museen zu Berlin constitutes an encyclopaedic museum, spread over many different sites, that aims to preserve, research, and display art treasures and cultural artefacts dating from all human history, and educate the public about their importance."
      },
      "relations": {
        "type": {
          "id": 2,
          "type": "organizertype",
          "attributes": {
            "name": "Museum",
            "description": "Collecting and showcasing things of cultural interest"
          }
        },
        "subjects": [
          {
            "id": 5,
            "type": "organizersubject",
            "attributes": {
              "name": "Komplexes Museum",
              "description": "Mehrere Museen mit unterschiedlichen Sammlungsschwerpunkten, die im selben Gebäude untergebracht sind"
            }
          }
        ],
        "address": {
          "id": 3,
          "type": "address",
          "attributes": {
            "street1": "Stauffenbergstraße 41",
            "street2": "Generaldirektion",
            "city": "Stauffenbergstraße 41",
            "zipCode": "10785"
          }
        }
      }
    }
  ],
  "meta": {
    "language": "en"
  }
}

