resources.description:
Creates a new invitation which also sends an email to the invited user

<!-- RESPONSE 200 -->
{
  "data": {
    "type": "invitation",
    "attributes": {
      "email": "invitee@kulturdaten.berlin",
      "user_id": 1,
      "created_at": "2021-04-27T12:41:04.960+02:00",
      "updated_at": "2021-04-27T12:41:04.961+02:00",
      "id": 1
    }
  },
  "meta": {
    "message": "Successfully issued invitation."
  }
}


<!-- ENDRESPONSE -->

<!-- RESPONSE 422 -->
{
  "errors": [
    {
      "rule": "required",
      "field": "email",
      "message": "required validation failed"
    }
  ]
}

<!-- ENDRESPONSE -->

<!-- RESPONSE 401 -->
{
  "errors": [
    {
      "title": "E_UNAUTHORIZED: The action you try to perform is only allowed for authenticated users.",
